    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        Set<Integer> cardCategoriesForSearch  = new HashSet<>();
        
        if (jCheckBox1.isSelected()) {
            cardCategoriesForSearch.add(0);
        }
        
        if (jCheckBox2.isSelected()) {
            cardCategoriesForSearch.add(1);
        }
                
        if (jCheckBox3.isSelected()) {
            cardCategoriesForSearch.add(2);
        }
                        
        if (jCheckBox4.isSelected()) {
            cardCategoriesForSearch.add(3);
        }
        
        if (jCheckBox10.isSelected()) {
            cardCategoriesForSearch.add(9);
        }
        
        cardFinder.setCardCategoryRestrictions(cardCategoriesForSearch);
        
        dispose();
    }//GEN-LAST:event_jButton2ActionPerformed

